import React from 'react';
import Dropdown from 'react-bootstrap/Dropdown'
import DropdownButton from 'react-bootstrap/DropdownButton'

const Filterbutton= props =>{

   let endarr = [];

    Object.entries(props.elements).map(([key,value])=>{
    let arr=[];
    for (let i = 0; i <value.length; i++) {
        arr.push(<Dropdown.Item as="button" onClick={()=>props.filterby(value[i])}>{value[i]}</Dropdown.Item>)
    }
    endarr.push(<DropdownButton id="dropdown-item-button" title={key}>
      {arr}
    </DropdownButton>)

 });


return (
 <table>
     <tr>
         {endarr.map(a=><td>{a}</td>)};
     </tr>
 </table>


)};
export default Filterbutton;
