import React, { useEffect } from 'react';
import {addWeeks,subWeeks,addMonths,subMonths,startOfWeek,addDays,format,isSameMonth,isSameDay,endOfWeek,startOfMonth,endOfMonth,parse} from 'date-fns';
import './calendar.css';
import {useState}  from 'react';
import ShowTime from '../pages/ShowTime';
import { relativeTimeThreshold } from 'moment';
import { Link } from 'react-router-dom';
import Calendarheader from './Calendarheader';
import Calendarday from './Calendarday';


const Calendarcell = props =>{

const newDate = new Date("2020-03-23");
const [currentWeek,setcurrentWeek] = useState(newDate);
const [currentMonth,setcurrentMonth] = useState(newDate);
const [selectedDate,setselectedDate] = useState(newDate);
const [currentYear,setcurrentYear] = useState(newDate.getFullYear());
const[check,setcheck] = useState(false);



    const startDate = startOfWeek(currentWeek);
    const endDate = endOfWeek(currentWeek);

    const dateFormat = "d";
    const rows = [];

    let days = [];
    let day = startDate;
    let formattedDate = "";

    while (day <= endDate) {

      for (let i = 0; i < 7; i++) {
        formattedDate = format(day, dateFormat);
        let sameday = isSameDay(day, selectedDate)? "selected" : "";
        let showtime = addDays(startDate,i);       
         let m = showtime.getMonth()+1;
         let month = showtime.getMonth()+1>=10? "":"0";
         let showtimesId = showtime.getFullYear() + "-" + month + m +"-"+ showtime.getDate(); 
        days.push(

          <div         
            className={`col cell ${sameday}` } 
            key={day} id={day} onClick={() => setselectedDate(addDays(startDate, i))}
            >
            <Link className ="link" to={`/showtime/${showtimesId}`}>
            <span className="number">{formattedDate}</span>
            <span className="bg">{formattedDate}</span>
            </Link>
          </div>

        );
        day = addDays(day, 1);
      }
      rows.push(
        <div className="row" key={day} >
          {days}
        </div>
      );
      days = [];
    }
    return <div className="body">{rows}</div>;
  }
  export default Calendarcell;
