import React,{useState,useEffect} from 'react';
import { useHttpClient } from '../../shared/hooks/http-hook';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import { useParams } from 'react-router-dom';
import MovieDescription from'./MovieDescription';
import ShowTimeList from '../../showtimes/components/ShowTimeList';

  const Description = props => {
  let movieid = useParams().movieId;
  const [LoadedDes, setLoadedDes] = useState();
  const { isLoading, error, sendRequest, clearError } = useHttpClient();
  const [Loadedshowtimes,setLoadedshowtimes] = useState();
  useEffect(() => {
     const fetchDes = async () => {
      setisLoading(true);
      try{          
        console.log(movieid);
        console.log(movieid);
        const response= await fetch(process.env.REACT_APP_BACKEND_URL + `/movies/${movieid}`); 
        const responseData = await response.json();
        console.log(responseData)
        if(!response.ok){
          console.log(responseData)
          throw new Error(responseData.message);
        }
        setLoadedDes(responseData.movie);
        setLoadedshowtimes(responseData.showtimes);

      } catch (err) {
        setError(err.message);
      }
      setisLoading(false);
    };

    fetchDes();
  },[movieid]);
  const errorHandler = () =>{
    setError=(null);
  }

  return (
    <React.Fragment>
    {isLoading && (<div><LoadingSpinner /></div>)}
    {!isLoading && LoadedDes && <MovieDescription des = {LoadedDes}/>}
    {!isLoading && LoadedDes&&<ShowTimeList items={Loadedshowtimes}/>}
    </React.Fragment>)

};




export default Description;
