import PlaceList from '../components/PlaceList';
import React, { useEffect,useState } from 'react';

import { useHttpClient } from '../../shared/hooks/http-hook';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import PaginationPage from '../../shared/components/UIElements/pagination';
const UserPlaces = () => {
  const[LoadedTheatres,setLoadedTheatres] = useState([]);
  const[isLoading,setisLoading] = useState(false);
  const[error,setError] = useState();
  const[currentPage,setcurrentPage] = useState(1);
  const postsPerPage = 20;
  const [currentPosts,setcurrentPosts] = useState([]);
  const [Sort,setSort] = useState("");
  const[Filter,setFilter] = useState("");
  const[Search,setSearch] = useState("");
  const[originaltheatres,setoriginaltheatres]=useState([]);
  const[filtertheatres,setfiltertheatres] = useState([]);


  useEffect(()=>{

    const fetchTheatres = async()=>{
      setisLoading(true);
      try{          
        const response= await fetch(`http://localhost:5000/api/theatres`); 
        const responseData = await response.json();
        if(!response.ok){

          throw new Error(responseData.message);
        }
        setoriginaltheatres(responseData.cinemas)
        setLoadedTheatres(responseData.cinemas);
      } catch (err) {
        setError(err.message);
      }
      setisLoading(false);
    };

    fetchTheatres();
  },[]);

  const indexOfLastPosts = currentPage * postsPerPage;
   const indexOfFirstPost = indexOfLastPosts - postsPerPage;
   const paginate = pageNumber => setcurrentPage(pageNumber);

   const sortby = method => {
    let theatres;
    if(method === "Name(A-Z)"){
       theatres = LoadedTheatres.sort((a,b)=>{
       setSort(method);
       if(a.cinema_name.toLowerCase() < b.cinema_name.toLowerCase()) return -1;
       if(a.cinema_name.toLowerCase() > b.cinema_name.toLowerCase()) return 1;
       return 0;
     })
    }else if(method === "Distance (nearby)"){
      setSort(method);
      theatres = LoadedTheatres.sort((a,b)=>{
      return a.distance - b.distance;
     })
    }
    setLoadedTheatres(theatres);
  }

 const searchtheatre = e =>{ 

   setSearch(e);
   let title;
   let zipcode;
   title = originaltheatres.filter((contact) =>{
     return contact.cinema_name.toLowerCase().split(' ').join('').indexOf(e.toLowerCase()) !== -1 ;
   });
   zipcode = originaltheatres.filter((contact) =>{
     return contact.postcode === parseInt(e) ;
  });
   setLoadedMovies(title === null ? postcode:title);
 }
 const elements = {
   "Distance":{"Distance":["within 20 miles","20 - 50 miles","beyond 50 miles","All"]}
 }
 const changeFilter = (k,v) => {
   let theatres;
   let valuearr = v.split(" ");
  if(k === "Distance"){
    setFilter("Distance");     
    let value = valuearr[valuearr.length-2];
    if(valuearr[0]==="All") {
      theatres = LoadedTheatres;
   }
    else if(valuearr[0] === "within"){
      theatres = LoadedTheatres.filter((contact)=>(contact.distance < parseInt(value)));
    }else if (valuearr[0] === "20"){   
       theatres = LoadedTheatres.filter((contact)=>(20 <= contact.distance && contact.distance < 50));
    }else{
      theatres = LoadedTheatres.filter((contact)=>(contact.distance >= 50));
    }

  }else if(k === "Clean Filter"){
    theatres = originaltheatres;
 }
 setLoadedTheatres(theatres);
}



  useEffect(()=>{
    const updatePosts=()=>{
    setcurrentPosts(LoadedTheatres.slice(indexOfFirstPost,indexOfLastPosts))};
    updatePosts()},[LoadedTheatres,currentPage,PaginationPage]);

  const errorHandler = () =>{
    setError=(null);
  }



  return(       
    <React.Fragment>
    <PaginationPage postsPerPage={postsPerPage} totalPosts={LoadedTheatres.length} currentpage={currentPage} paginate={paginate}/>
    {isLoading && (<div><LoadingSpinner /></div>)}
    {!isLoading && LoadedTheatres && <PlaceList items={currentPosts} />}
     </React.Fragment>)
  };

export default UserPlaces;
