import React from 'react';
import Dropdown from 'react-bootstrap/Dropdown'
import DropdownButton from 'react-bootstrap/DropdownButton'
import Button from '../FormElements/Button';
const Filterbutton= props =>{
   let changeTitle = (e) =>{
    props.filterby(key,e);
   }
   let endarr = [];

    Object.entries(props.elements).map(([key,value])=>{
    let arr=[];
    for (let i = 0; i <value.length; i++) {
        arr.push(<Dropdown.Item as="button" onClick={changeTitle(key,value[i])}>{value[i]}</Dropdown.Item>)
    }
    endarr.push(<DropdownButton id="dropdown-item-button" title={key}>
      {arr}
    </DropdownButton>)


 });
 endarr.push(<Button onClick={()=>props.filterby("Clean Filter",0)}>No Filter</Button>)

return (
 <table>
     <tr>
         {endarr.map(a=><td>{a}</td>)}
     </tr>
 </table>


)};
export default Filterbutton;
