import React from 'react';
import Card from '../UIElements/Card';
import { useParams } from 'react-router-dom';
import StripeCheckout from 'react-stripe-checkout';

import axios from "axios";
import {toast} from "react-toastify";
import Button from './Button';
import './Buy.css';

toast.configure();
const Buy = props => {
    let movie = useParams().movieid;
    let theatre =useParams().theatreid;
    let moviename= useParams().moviename;
    let theatrename = useParams().theatrename;
    const [product] = React.useState(
      {
      movieid:movie,
      theatreid:theatre,
      moviename:moviename,
      theatrename:theatrename,
      price:15},
    )
    const buttonstyle={
      background:"#FF7417",
      backgroundImage:"#FF7417"
    }
 async function handleToken(token){

 const response = await axios.post('http://localhost:5000/cart',{token,product});
 const {status} = response.data
 if(status === 'success'){
   toast("Success! Check email for details!",{type:"success"});
 }else{
   toast("Something went wrong",{type:"error"});
 }
}
  return (<Card className="ticket">
      <table>
  <tr>
      <td className="tickettitle"><strong>Your Ticket</strong>
      </td>

  </tr>
  <tr>
  <td className="ticketline">
    Movie: {product.moviename}
      </td>

  </tr>
  <tr>
    <td className="ticketline">
    Location: {product.theatrename}
    </td>
  </tr>  
  <tr>
    <td className="ticketline">
    Price: ${product.price}
    </td>
  </tr>  
  <tr>
    <StripeCheckout 
    stripeKey="pk_test_P75FnWrf1B5LPGyovDcsMfok00OAcSj1KQ"
    token={handleToken}
    billingAddress
    shippingAddress
    amount={product.price * 100}
    name={product.movieid}
    style={buttonstyle}
    />
  </tr>


 </table>
  </Card>);


}

export default Buy;
